import React from 'react';
import { connect } from 'react-redux';
import { addItem, getExpense, getExpenseThunk } from '../actions/index';
import getCurrentCoins from '../services/walletAPI';

class Form extends React.Component {
  constructor() {
    super();
    this.state = {
      value: 0,
      description: '',
      wallet: '',
      payment: '',
      tag: '',
    };
  }

  componentDidMount() {
    getCurrentCoins();
    getExpenseThunk();
  }

  // funcao generica para adicionar os dados de input ao estado do componente
  handleChange = ({ target }) => {
    const { id, value } = target;
    // condicao para add o value como number do campo input
    if (id === 'value') {
      this.setState({ [id]: Number(value) });
    } else {
      this.setState({ [id]: value });
    }
  }

  // funcao para adicionar o estado do componente ao global
  addRegister = (e) => {
    e.preventDefault();
    const { dispatchSetData, totalExpense, expenses, dispatchSetCoins } = this.props;
    dispatchSetData(this.state);

    dispatchSetCoins(getCurrentCoins());
    console.log(getExpenseThunk());
    // const soma = expenses.reduce((acumulador, valorAtual) => acumulador + valorAtual.value, totalExpense);
    // function getTotal(total, item) {
    //   return (total + item.value);
    // }
    // const soma = expenses.reduce(getTotal, 0);

    // console.log(soma);
  }

  render() {
    return (
      <form onSubmit={ this.addRegister }>
        <label htmlFor="value">
          { }
          Valor
          <input type="number" id="value" data-testid="value-input" onChange={ this.handleChange } />
        </label>
        <label htmlFor="description">
          { ' ' }
          Descrição
          <input type="text" id="description" data-testid="description-input" onChange={ this.handleChange } />
        </label>
        <label htmlFor="wallet">
          { ' ' }
          Moeda
          <select data-testid="currency-input" id="wallet" onChange={ this.handleChange }>
            {/* <option value="BRL">Real</option> */ }
          </select>
        </label>
        <label htmlFor="payment">
          { ' ' }
          Método de Pagamento
          <select data-testid="method-input" id="payment" onChange={ this.handleChange }>
            <option>Dinheiro</option>
            <option>Cartão de Crédito</option>
            <option>Cartão de Débito</option>
          </select>
        </label>
        <label htmlFor="tag">
          { ' ' }
          Tag
          <select data-testid="tag-input" id="tag" onChange={ this.handleChange }>
            <option>Alimentação</option>
            <option>Lazer</option>
            <option>Trabalho</option>
            <option>Transporte</option>
            <option>Saúde</option>
          </select>
        </label>
        <button type="submit">Adicionar Despesa</button>

      </form>
    );
  }
}

const mapDispatchToProps = (dispatch) => ({
  dispatchSetData: (value) => dispatch(addItem(value)),
  dispatchSetCoins: (value) => dispatch(getExpense(value)),
});

const mapStateToProps = (state) => ({
  totalExpense: state.wallet.totalExpense,
  expenses: state.wallet.expenses,
});

export default connect(mapStateToProps, mapDispatchToProps)(Form);
