import React from 'react';
import { connect } from 'react-redux';
import propTypes from 'prop-types';
import { addItem, getExpenseThunk } from '../actions/index';
import getCurrentCoins from '../services/walletAPI';

class Form extends React.Component {
  constructor() {
    super();
    this.state = {
      id: 0,
      value: 0,
      description: '',
      wallet: 'USD',
      payment: '',
      tag: '',
      exchangeRates: [],
    };
  }

  componentDidMount() {
    const { dispatchSetCoins } = this.props;
    dispatchSetCoins();
  }

  // funcao generica para adicionar os dados de input ao estado do componente
  handleChange = ({ target }) => {
    const { id, value } = target;
    // condicao para add o value como number do campo input
    if (id === 'value') {
      this.setState({ [id]: Number(value) });
    } else {
      this.setState({ [id]: value });
    }
  }

  // funcao para adicionar o estado do componente ao global
  addRegister = async (e) => {
    e.preventDefault();
    const { dispatchSetData, expenses } = this.props;

    const resultAPI = await getCurrentCoins();

    // atualiza o estado do componente com as chaves abaixo e despacha
    this.setState({ exchangeRates: resultAPI, id: expenses.length });
    // const { exchangeRates } = this.state;
    dispatchSetData(this.state);
    const result = expenses.reduce((acc, item) => {
      const value = Number(item.value) * Number(item.exchangeRates[item.wallet].ask);
      console.log(value);
      return acc + value;
    }, 0);

    console.log(result);
  }

  render() {
    const { currencies } = this.props;
    return (
      <form onSubmit={ this.addRegister }>
        <label htmlFor="value">
          { }
          Valor
          <input
            type="number"
            id="value"
            data-testid="value-input"
            onChange={ this.handleChange }
          />
        </label>
        <label htmlFor="description">
          { ' ' }
          Descrição
          <input
            type="text"
            id="description"
            data-testid="description-input"
            onChange={ this.handleChange }
          />
        </label>
        <label htmlFor="wallet">
          { ' ' }
          Moeda
          <select data-testid="currency-input" id="wallet" onChange={ this.handleChange }>
            {
              currencies.map((i) => (<option key={ i } value={ i }>{ i }</option>))
            }
          </select>
        </label>
        <label htmlFor="payment">
          { ' ' }
          Método de Pagamento
          <select data-testid="method-input" id="payment" onChange={ this.handleChange }>
            <option>Dinheiro</option>
            <option>Cartão de Crédito</option>
            <option>Cartão de Débito</option>
          </select>
        </label>
        <label htmlFor="tag">
          { ' ' }
          Tag
          <select data-testid="tag-input" id="tag" onChange={ this.handleChange }>
            <option>Alimentação</option>
            <option>Lazer</option>
            <option>Trabalho</option>
            <option>Transporte</option>
            <option>Saúde</option>
          </select>
        </label>
        <button type="submit">Adicionar Despesa</button>

      </form>
    );
  }
}

const mapDispatchToProps = (dispatch) => ({
  dispatchSetData: (value) => dispatch(addItem(value)),
  dispatchSetCoins: (value) => dispatch(getExpenseThunk(value)),
});

const mapStateToProps = (state) => ({
  totalExpense: state.wallet.totalExpense,
  expenses: state.wallet.expenses,
  currencies: state.wallet.currencies,
});

Form.propTypes = {
  currencies: propTypes.object,
}.isRequired;

export default connect(mapStateToProps, mapDispatchToProps)(Form);
