import React from 'react';

class Form extends React.Component {
  constructor() {
    super();
    this.state = {
      value: '',
      description: '',
      wallet: '',
      payment: '',
      tag: '',
    };
  }

  render() {
    return (
      <form onSubmit={ this.addRegister }>
        <label htmlFor="value">
          {' '}
          Valor
          <input type="number" id="value" data-testid="value-input" />
        </label>
        <label htmlFor="description">
          {' '}
          Descrição
          <input type="text" id="description" data-testid="description-input" />
        </label>
        <label htmlFor="wallet">
          {' '}
          Moeda
          <select data-testid="currency-input" id="wallet">
            {/* <option value="BRL">Real</option> */}
          </select>
        </label>
        <label htmlFor="payment">
          {' '}
          Método de Pagamento
          <select data-testid="method-input" id="payment">
            <option>Dinheiro</option>
            <option>Cartão de Crédito</option>
            <option>Cartão de Débito</option>
          </select>
        </label>
        <label htmlFor="tag">
          {' '}
          Tag
          <select data-testid="tag-input" id="tag">
            <option>Alimentação</option>
            <option>Lazer</option>
            <option>Trabalho</option>
            <option>Transporte</option>
            <option>Saúde</option>
          </select>
        </label>
        <button type="submit">Adicionar Despesa</button>

      </form>
    );
  }
}

export default Form;
