import React from 'react';
import { connect } from 'react-redux';
import { MIN_CHARACTER } from '../lib/constants';
import { login as loginAction } from '../store/actions';

class Login extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      email: '',
      loginButtonDisabled: true,
    };
  }

handleSubmit =(e) => {
  e.preventDefault();
  const { email } = this.state;
  const { login, history } = this.props;
  const verify = email.length >= MIN_CHARACTER;

  login({ email });
  history.push('/carteira');
}

render() {
  return (
    <main>
      <form onSubmit={ this.handleSubmit }>
        <input
          type="email"
          data-testid="email-input"
          onChange={ (e) => this.setState({ email: e.target.value }) }
          required
        />
        <input type="password" data-testid="password-input" required />
        <button type="submit" disabled={ loginButtonDisabled }>Entrar</button>
      </form>
    </main>
  );
}
}

const mapDispatchToProps = (dispatch) => ({
  login: (value) => dispatch(loginAction(value)),
});

export default connect(null, mapDispatchToProps)(Login);
